import React, { useState } from "react";
import './App.css';

function App() {
  const [ingredients, setIngredients] = useState([]);
  const [steps, setSteps] = useState([]);
  const [author, setAuthor] = useState("");
  const [loading, setLoading] = useState(false);
  const [url, setUrl] = useState("");

  const handleParse = async () => {
    setLoading(true);
    try {
      const data = await parseWebsite(url);
      setIngredients(data.ingredients || []);
      setSteps(data.steps || []);
      setAuthor(data.author || "");
    } catch (err) {
      alert(err.message);
    } finally {
      setLoading(false);
    }
  };

  const parseWebsite = async () => {
    setLoading(true);

    setIngredients(["A,B,C"]);
    setSteps(["1,2,3"]);
    setAuthor(["Recipe by ME"]);
  }
  return (
    <div className="container">
      <header>
        <button onClick={parseWebsite}>Parse Recipe</button>
      </header>

      <div className="panels">
        <Panel title="Ingredients">
          <ul>
            {ingredients.map((item, i) => (
              <li key={i}>{item}</li>
            ))}
          </ul>
        </Panel>

        <Panel title="Steps">
          <ol>
            {steps.map((step, i) => (
              <li key={i}>{step}</li>
            ))}
          </ol>
        </Panel>

        <Panel title="Author">
          <p>{author}</p>
        </Panel>
      </div>
    </div>
  );
}

function Panel({ title, children }) {
  return (
    <div className="panel">
      <h2>{title}</h2>
      {children}
    </div>
  );
}

export default App;
